mapper.select:
SELECT COUNT(*) FROM item_report

	
		category_num = #{category}
	
	
		and INSTR(reason,#{kwd}) > 0

SELECT sellerReport_num,name, productcode, ir.memberidx, reason, TO_CHAR(report_date,'YYYY-MM-DD') report_date, ir.category_num, category_name , TO_CHAR(processdate,'YYYY-MM-DD')processdate , TO_CHAR(releasedate, 'YYYY-MM-DD')releasedate FROM item_report ir LEFT OUTER JOIN member_info m ON m.memberidx = ir.memberidx LEFT OUTER JOIN report_category rc ON rc.category_num = ir.category_num
ir.category_num = #{category}
and INSTR(reason,#{kwd}) > 0

SELECT COUNT(*) FROM ProductReviewReport

	
		category_num = #{category}
	
	
		and INSTR(content,#{kwd}) > 0

SELECT report_code, prr.memberidx, review_num, prr.category_num, category_name, content, TO_CHAR(report_date, 'YYYY-MM-DD') report_date, TO_CHAR(processdate,'YYYY-MM-DD')processdate, name FROM ProductReviewReport prr LEFT OUTER JOIN member_info m ON m.memberidx = prr.memberidx LEFT OUTER JOIN report_category rc ON rc.category_num = prr.category_num
prr.category_num = #{category}
and INSTR(content,#{kwd}) > 0
