mapper.update:
UPDATE CART_ITEM
SET QUANTITY = #{quantity}
WHERE CART_ITEM_CODE = #{cartItemCode}

mapper.select:
SELECT CART_ITEM_CODE AS cartItemCode, c.MEMBERIDX AS memberIdx, p.ITEM, p.DISCOUNT, c.PRODUCTCODE AS productCode, QUANTITY, p.PRICE, CREATED, CART_OPTION AS cartOption, s.BRAND_CODE as brandCode, s.BRANDNAME as brandName, p1.OPTION_NAME as optionName, p1.OPTION_PRICE as optionPrice FROM CART_ITEM c LEFT JOIN PRODUCT p ON p.PRODUCTCODE = c.PRODUCTCODE LEFT JOIN PRODUCTOPTION p1 ON p.PRODUCTCODE = p1.PRODUCTCODE LEFT JOIN SELLER_BRAND s ON p.BRAND_CODE = s.BRAND_CODE WHERE c.MEMBERIDX = #{memberIdx}
AND CART_ITEM_CODE IN
#{code}

mapper.delete:
DELETE FROM CART_ITEM
WHERE MEMBERIDX = #{memberIdx}
AND CART_ITEM_CODE IN

    #{code}
